import React from 'react';

import './modal.css';
import {Button, Modal, ModalHeader, ModalBody, ModalFooter, FormGroup, Input, Label} from 'reactstrap';
import productos from '../lensun principal/principla/len';

class Contacto extends React.Component{
  state={
    abierto: false,
  }

  abrirModal=()=>{
    this.setState({abierto: !this.state.abierto});
  }

  render(){

    const modalStyles={
     
      position: "absolute",
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      innerHeight:  ""
    }
    return(
      <>
      <body className='bodymodal'> 
      <Button className='botonPrincipal' onClick={this.abrirModal}>Contacto</Button>

      

      <Modal isOpen={this.state.abierto} style={modalStyles} >
        <ModalHeader>
          <img height={320} src={productos[11].img  }/>
        </ModalHeader>
        <ModalBody>
          <FormGroup>
            <Label for="nombre">NOMBRE</Label>
            <Input type="email" name='email'/> 
          
            <Label for="subject">ASUNTO</Label>
            <Input type='text' name='subject'/> 

            <label for= "comment">Comentario</label>
            <textarea name='comments' cols="15" row= "15"></textarea>
          </FormGroup>
        </ModalBody>

        <ModalFooter>
            
            <Button color="secondary" onClick={this.abrirModal}>Cerrar</Button>
        </ModalFooter>
      </Modal>
      </body>
      </>
    )
  }
}

export default Contacto;
