import React from 'react';


import {Button, Modal, ModalHeader, ModalBody, ModalFooter} from 'reactstrap';
import productos from '../../lensun principal/principla/len';

class Custom2 extends React.Component{
  state={
    abierto: false,
  }

  abrirModal=()=>{
    this.setState({abierto: !this.state.abierto});
  }

  render(){

    const modalStyles={
     
      position: "absolute",
      top: '50%',
      left: '50%',
      transform: 'translate(-50%, -50%)',
      innerHeight:  ""
    }
    return(
      <>
      <body className='bodymodal'> 
      <Button className='botonPrincipal' onClick={this.abrirModal}>inform</Button>

      

      <Modal isOpen={this.state.abierto} style={modalStyles} >
        <ModalHeader>
          <img height={300} src={productos[11].img  }/>
        </ModalHeader>
        <ModalBody>
        <h1>CustomPro2</h1>
        <h2>Kit Inicial:</h2>
        <h3>30 Film Prueba<br/>5 Film Tablet (3 8" y 2 "10")<br/>10 Paño de Limpieza<br/>Pad instalacion anti deslizante <br/>Espatula 23cm<br/>Espatula 15cm </h3>
        </ModalBody>

        <ModalFooter>
            
            <Button color="secondary" onClick={this.abrirModal}>Cerrar</Button>
        </ModalFooter>
      </Modal>
      </body>
      </>
    )
  }
}

export default Custom2;
